import { Component } from '@angular/core';
import { Router } from '@angular/router'; // Import du Router
import { AuthService } from '../auth.service';
import { Compte } from '../compte.interface';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: Compte['nomUtilisateur'] = '';
  password: Compte['motDePasse'] = '';

  constructor(
    private authService: AuthService,
    private router: Router // Injection du Router
  ) {}

  login(): void {
    // Appel de la méthode de connexion de AuthService
    // Utilisation de subscribe pour attendre la résolution de l'observable
    this.authService.login(this.username, this.password).subscribe((loggedIn) => {
      if (loggedIn) {
        // Redirection vers la page 'modules' si la connexion réussit
        this.router.navigate(['/modules']);
      } else {
        // Gérer l'échec de la connexion ici (par exemple, afficher un message d'erreur)
      }
    });
  }
}
<div>
  <label for="username">Nom d'utilisateur:</label>
  <!-- Utilisation de ngModel pour lier la propriété username -->
  <input type="text" id="username" name="username" [(ngModel)]="username">
</div>
<div>
  <label for="password">Mot de passe:</label>
  <!-- Utilisation de ngModel pour lier la propriété password -->
  <input type="password" id="password" name="password" [(ngModel)]="password">
</div>
<button (click)="login()">Se connecter</button>
