import { Component, Input, OnInit } from '@angular/core';
import { CategorieService } from '../services/categorie.service';
import Categorie from '../model/categorie';

@Component({
  selector: 'app-categorie',
  templateUrl: './categorie.component.html',
  styleUrls: ['./categorie.component.css']
})
export class CategorieComponent implements OnInit {
  @Input() categories: Categorie[] = [];

  constructor(private categorieService: CategorieService) { }

  ngOnInit(): void {
    this.loadCategories();
  }

  loadCategories(): void {
    this.categorieService.getAllCategories().subscribe(
      (data: Categorie[]) => {
        this.categories = data;
      },
      (error) => {
        console.error('Error fetching categories:', error);
      }
    );
  }
}
<div class="container-xxl py-5 category">
    <div class="container">
        <div class="text-center wow fadeInUp" data-wow-delay="0.1s">
            <h6 class="section-title bg-white text-center text-primary px-3">Categories</h6>
            <h1 class="mb-5">Courses Categories</h1>
        </div>
        <div class="row g-3">
            <div class="col-lg-7 col-md-6">
                <div class="row g-3">
                    <div class="col-lg-12 col-md-12" *ngFor="let category of categories">
                        <div class="card">
                            <img [src]="category.image" class="card-img-top" alt="...">
                            <div class="card-body">
                                <h5 class="card-title">{{ category.nom }}</h5>
                                <p class="card-text">{{ category.description }}</p>
                                <a href="#" class="btn btn-primary" (click)="editCategory(category)">Editer</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div class="col-lg-5 col-md-6" *ngIf="selectedCategory">
                <div class="card">
                    <img [src]="selectedCategory.image" class="card-img-top" alt="...">
                    <div class="card-body">
                        <h5 class="card-title">
                            <input type="text" class="form-control" [(ngModel)]="selectedCategory.nom">
                        </h5>
                        <p class="card-text">
                            <textarea class="form-control" [(ngModel)]="selectedCategory.description"></textarea>
                        </p>
                        <button class="btn btn-success" (click)="updateCategory(selectedCategory)">Enregistrer</button>
                        <button class="btn btn-danger" (click)="cancelEdit()">Annuler</button>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
