import { Component } from '@angular/core';
import { Router } from '@angular/router'; // Import du Router
import { AuthService } from '../auth.service';
import { Compte } from '../model/compte';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = ''; // Ajoutez un type explicite et une valeur par défaut
  password: string = ''; // Ajoutez un type explicite et une valeur par défaut
  newUsername: string = ''; // Ajoutez le champ de nouvel utilisateur
  newPassword: string = ''; 
  constructor(
    private authService: AuthService,
    private router: Router // Injection du Router
  ) {}

  login(): void {
    this.authService.login(this.username, this.password).subscribe(response => {
      if (response && response.token) {
        this.authService.setToken(response.token); // Stockez le token si la connexion est réussie
        this.router.navigate(['/modules']); // Redirection
      } else {
        // Gérer l'échec de la connexion ici
      }
    }, error => {
      console.log(error);
      // Gérer l'échec de la connexion ici
    });
  }
  
  register(): void {
    this.authService.register(this.newUsername, this.newPassword).subscribe((registered) => {
      if (registered) {
        // Rediriger vers la page de connexion après l'inscription réussie
        this.router.navigate(['/login']);
      } else {
        // Gérer l'échec de l'inscription
      }
    });
  }

}
<div class="form-group">
  <label for="username">Nom d'utilisateur</label>
  <input type="text" id="username" name="username" [(ngModel)]="username">
</div>
<div class="form-group">
  <label for="password">Mot de passe</label>
  <input type="password" id="password" name="password" [(ngModel)]="password">
</div>
<div class="form-group">
  <button (click)="login()">Se connecter</button>
</div>


<div class="form-group">
  <h2>Inscription</h2>
  <label for="newUsername">Nouveau nom d'utilisateur</label>
  <input type="text" id="newUsername" name="newUsername" [(ngModel)]="newUsername">
</div>
<div class="form-group">
  <label for="newPassword">Nouveau mot de passe</label>
  <input type="password" id="newPassword" name="newPassword" [(ngModel)]="newPassword">
</div>
<div class="form-group">
  <button (click)="register()">S'inscrire</button>
</div>
