import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string;
  password: string;

  constructor(private authService: AuthService, private router: Router) {}

  onSubmit(): void {
    this.authService.login(this.username, this.password).subscribe((success) => {
      if (success) {
        // Rediriger vers la page d'accueil ou toute autre page après la connexion réussie
        this.router.navigate(['/home']);
      } else {
        // Gérer les erreurs de connexion, afficher un message d'erreur, etc.
      }
    });
  }
}
<form (ngSubmit)="onSubmit()">
  <div>
    <label for="username">Nom d'utilisateur</label>
    <input type="text" id="username" name="username" [(ngModel)]="username">
  </div>
  <div>
    <label for="password">Mot de passe</label>
    <input type="password" id="password" name="password" [(ngModel)]="password">
  </div>
  <button type="submit">Se connecter</button>
</form>
