import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string;
  password: string;

  constructor(private authService: AuthService, private router: Router) {}

  onSubmit(): void {
    this.authService.login(this.username, this.password).subscribe((success) => {
      if (success) {
        // Rediriger vers la page d'accueil ou toute autre page après la connexion réussie
        this.router.navigate(['/home']);
      } else {
        // Gérer les erreurs de connexion, afficher un message d'erreur, etc.
      }
    });
  }
}
<div class="container">
  <div class="row">
    <div class="col-md-6 offset-md-3">
      <h2>Login</h2>
      <form (ngSubmit)="login(username.value, password.value); $event.preventDefault()">

        <div class="form-group">
          <label for="username">Username</label>
          <input type="text" id="username" class="form-control" #username required>
        </div>
        <div class="form-group">
          <label for="password">Password</label>
          <input type="password" id="password" class="form-control" #password required>
        </div>
        <button type="submit" class="btn btn-primary" >Login</button>

      </form>
    </div>
  </div>
</div>
