import { Component } from '@angular/core';
import { LoginService } from './services/login.service';
import { ModuleService } from '../services/module.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  modules: any[] = [];

  constructor(private loginService: LoginService, private moduleService: ModuleService, private router: Router,) { }

  login(username: string, password: string): void {
    this.loginService.login(username, password).subscribe(
      response => {
        const etudiantId = response.etudiantId;
        this.moduleService.getModulesByEtudiantId(etudiantId).subscribe( modules => {
            this.modules = modules;
            this.router.navigate(['/modules']);
          },
          error => {
            console.error('Erreur lors de la récupération des modules de l\'étudiant :', error);
          }
        );
      },
      error => {
        console.error('Erreur lors de la connexion :', error);
      }
    );
  }
}
<form (ngSubmit)="login(username.value, password.value)">
  <!-- Email input -->
  <div class="form-outline mb-4">
    <input type="email" id="username" class="form-control" #username />
    <label class="form-label" for="username">Email address</label>
  </div>

  <!-- Password input -->
  <div class="form-outline mb-4">
    <input type="password" id="password" class="form-control" #password />
    <label class="form-label" for="password">Password</label>
  </div>

  <!-- Checkbox -->
  <div class="form-check d-flex justify-content-center mb-4">
    <input class="form-check-input me-2" type="checkbox" value="" id="form2Example33" checked="false" />
    <label class="form-check-label" for="form2Example33">
      Remember me
    </label>
  </div>

  <!-- Submit button -->
  <div class="text-center">
    <button type="submit" class="btn btn-primary btn-block mb-4">
      Sign In
    </button>
  </div>
</form>
